import {LOGIN_USER, REGISTER_USER, AUTH_USER, LOGOUT_USER, ADMIN_USER} from './types';
import axios from 'axios';

export function register(user) {
  const request = axios.post('http://localhost:6000/api/users', user)
    .then(response => response.data);

  return {
    type: REGISTER_USER,
    payload: request
  }
}

export function login(user){
  const request = axios.post('http://localhost:6000/api/login', user)
    .then(response => response.data)

  return {
    type: LOGIN_USER,
    payload: request
  } 
}

export function auth(){
  const request = axios.get('http://localhost:6000/api/users/auth')
  .then(response => response.data);

  return {
    type: AUTH_USER,
    payload: request
  }
}

export function admin(){
  const request = axios.get(`http://localhost:6000/api/users/auth`)
  .then(response => response.data);

  return {
      type: ADMIN_USER,
      payload: request
  }
}

export function logoutUser(){
  const request = axios.get(`http://localhost:6000/api/users/logout`)
  .then(response => response.data);

  return {
      type: LOGOUT_USER,
      payload: request
  }
}
