import axios from 'axios';
import {
  GET_ITEMS,
  ADD_ITEM,
  ITEMS_LOADING
} from './types';
import { tokenConfig } from './authActions';
import { returnErrors } from './errorActions';

export const getItems = () => (dispatch, getState) => {
  dispatch({ type: ITEMS_LOADING });

  axios.get('http://localhost:5000/api/items', tokenConfig(getState))
    .then(res =>
      dispatch({
        type: GET_ITEMS,
        payload: res.data
      })
    )
    .catch(err =>
      dispatch(returnErrors(err.response.data, err.response.status))
    );
}

export const addItem = ({ departmentName, listOfAssets, uniqueId }) => (dispatch, getState) => {
  // Headers
  const config = {
    headers: {
      'Content-Type': 'application/json'
    }
  }

  // Request body
  const body = JSON.stringify({ departmentName, listOfAssets, uniqueId });

  axios.post('http://localhost:5000/api/items', config, body, tokenConfig(getState))
    .then(res =>
      dispatch({
        type: ADD_ITEM,
        payload: console.log(res.data)
      })
    )
    .catch(err =>
      dispatch(returnErrors(err.response.data, err.response.status))
    );
}
