import React, {Component} from 'react';
import images from '../images/images.jpeg';
import side from '../images/side.png';
// import { register } from '../components/UserFunctions';
import axios from 'axios';
import {REGISTERED} from '../actions/types';

class Register extends Component {
    constructor(props) {
        super(props);

        this.state = {
          user: {
            firstname: '',
            lastname: '',
            email: '',
            password: ''
          }
        };
        this.onChange = this.onChange.bind(this)
        this.onSubmit = this.onSubmit.bind(this)
      }

      onSubmit(e){
        e.preventDefault();

          const user = {
            firstname: this.state.firstname,
            lastname: this.state.lastname,
            email: this.state.email,
            password: this.state.password
          }
          const request = axios.post('http://localhost:5000/api/users', user)
          return {
            type: REGISTERED,
            payload: request
          }
          // register(user).then(res => {
          //   if(res) {
          //     this.props.history.push(`\login`);
          //   }
          // })
        }

      componentDidUpdate(nextItem) {
        localStorage.setItem('user', JSON.stringify(nextItem))
      }

      onChange (e){
        this.setState({
          [e.target.name]: e.target.value
        })

      };

    render() {
        const { formErrors } = this.state;
        return (
          <>
            <img src={images} className="backside"/>
                <div className="wrapper">
                <div className="form-wrapper">
                    <h1>Login</h1>
                    <form onSubmit={this.onSubmit} noValidate>

                    <div className="first_name">
                        <label htmlFor="first_name">First name</label>
                        <input
                        placeholder="first_name"
                        type="first_name"
                        name="firstname"
                        noValidate
                        value={this.state.firstname}
                        onChange={this.onChange}
                        />
                    </div>

                    <div className="last_name">
                        <label htmlFor="last_name">Last name</label>
                        <input
                        placeholder="last_name"
                        type="last_name"
                        name="lastname"
                        noValidate
                        value={this.state.lastname}
                        onChange={this.onChange}
                        />
                    </div>

                    <div className="email">
                        <label htmlFor="email">Email</label>
                        <input
                        placeholder="Email"
                        type="email"
                        name="email"
                        noValidate
                        value={this.state.email}
                        onChange={this.onChange}
                        />
                    </div>
                    <div className="password">
                        <label htmlFor="password">Password</label>
                        <input
                        placeholder="Password"
                        type="password"
                        name="password"
                        noValidate
                        value={this.state.password}
                        onChange={this.onChange}
                        />
                    </div>
                    <div className="createAccount">
                        <button type="submit">Register</button>
                    </div>
                    </form>
                </div>
                </div>
        </>    
        )
    }
}
export default Register;
