import React, { Component } from 'react'
import { Table, Container } from 'react-bootstrap'
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { addItem } from '../actions/tableActions';

export class Networking extends Component {
  constructor(props) {
    super(props)

    this.state = {
      departmentName: '',
      listOfAssets: '',
      uniqueId: '',
      tableContent: []
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleClick = this.handleClick.bind(this);
  }

  static propTypes = {
    auth: PropTypes.object.isRequired
  }

  handleClick(e) {
    e.preventDefault();

    let randomID = Math.floor(Math.random() * 999999);
    let randomId = Math.floor(Math() * 3);

    const { departmentName, listOfAssets, uniqueId, tableContent } = this.state

    let newTab = this.state.tableContent

    const newItem = {
      // key,
      departmentName,
      listOfAssets,
      uniqueId
    }

    newTab.push({
      id: randomId,
      key: randomID,
      uniqueId: randomID,
      departmentName,
      listOfAssets
    });

    this.setState({
      tableContent: newTab
    })

    console.log(tableContent)
    addItem(newTab);
  }

  handleChange(event) {
    this.setState({
      [event.target.name]: event.target.value
    });
  }

  render() {
    const { isAuthenticated, user } = this.props.auth;

    const admin = (
      <form onSubmit={this.handleClick}>
        <label htmlFor="DepartmentName">Department Name</label>
        <input type="text" name="departmentName" onChange={this.handleChange} />

        <label htmlFor="Asset">Asset</label>
        <input type="text" name="listOfAssets" onChange={this.handleChange} />

        <label htmlFor="UniqueId">Unique Id</label>
        <input type="text" name="uniqueId" onChange={this.handleChange} />

        <button type="button" id="add" onClick={this.handleClick}>Add item</button>
      </form>
    )
    return (
      <section className="App">
        <header>
          <h1>Network Department</h1>
        </header>

        <section>
          <Container>
            {
              isAuthenticated && user.isAdmin ? admin : console.log('bad')
            }
          </Container>

          <section>
            <Container>

              {/* <h1>Items</h1> */}
              <Table striped bordered hover>
                <thead>
                  <tr>
                    <th>#</th>
                    <th>Department Name</th>
                    <th>Asset</th>
                    <th>Unique Id</th>
                  </tr>
                </thead>
                <tbody>
                  {this.state.tableContent.map((newItem) =>
                    // console.log(newItem)
                    <tr key={newItem.key}>
                      <td>{newItem.id}</td>
                      <td>{newItem.departmentName}</td>
                      <td>{newItem.listOfAssets}</td>
                      <td>{newItem.uniqueId}</td>
                      {/* <td> */}
                      {/* <span>new</span> */}
                      {/* </td> */}
                    </tr>
                  )}
                </tbody>
              </Table>
            </Container>
          </section>
        </section>
      </section>
    );
  }
}

const mapStateToProps = state => ({
  auth: state.auth,
  item: state.item
})

export default connect(mapStateToProps, { addItem })(Networking)
