import React, { Component } from 'react'
import { Table, Container } from 'react-bootstrap'
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { addItem } from '../actions/tableActions';

export class Networking extends Component {
  constructor(props) {
    super(props)

    this.state = {
      departmentName: '',
      listOfAssets: '',
      uniqueId: '',
      tableContent: []
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleClick = this.handleClick.bind(this);
  }

  static propTypes = {
    auth: PropTypes.object.isRequired
  }

  handleClick(e) {
    e.preventDefault();

    const { departmentName, listOfAssets, uniqueId } = this.state

    const newItem = {
      departmentName,
      listOfAssets,
      uniqueId
    }

    console.log(addItem(newItem));
  }

  handleChange(event) {
    this.setState({
      [event.target.name]: event.target.value
    });
  }

  render() {
    const { isAuthenticated, user } = this.props.auth;

    const admin = (
      <form onSubmit={this.handleClick}>
        <label htmlFor="DepartmentName">Department Name</label>
        <input type="text" name="departmentName" onChange={this.handleChange} />

        <label htmlFor="Asset">Asset</label>
        <input type="text" name="listOfAssets" onChange={this.handleChange} />

        <label htmlFor="UniqueId">Unique Id</label>
        <input type="text" name="uniqueId" onChange={this.handleChange} />

        <button type="button" id="add" onClick={this.handleClick}>Add item</button>
      </form>
    )
    return (
      <section className="App">
        <header>
          <h1>Network Department</h1>
        </header>

        <section>
          <Container>
            {
              isAuthenticated && user.isAdmin ? admin : console.log('bad')
            }
          </Container>

          <section>
            <Container>

              {/* <h1>Items</h1> */}
              <Table striped bordered hover>
                <thead>
                  <tr>
                    <th>#</th>
                    <th>Department Name</th>
                    <th>Asset</th>
                    <th>Unique Id</th>
                  </tr>
                </thead>
                <tbody>
                  {this.state.tableContent.map((item) =>
                    <tr key={item.key}>
                      <td>{item.title}</td>
                      <td>{item.amount}</td>
                      {/* <td> */}
                      {/* Here add the onClick for the action "remove it" on the span */}
                      {/* <span>Remove it</span> */}
                      {/* </td> */}
                      <td></td>
                    </tr>
                  )}
                </tbody>
              </Table>
            </Container>
          </section>
        </section>
      </section>
    );
  }
}

const mapStateToProps = state => ({
  auth: state.auth,
  item: state.item
})

export default connect(mapStateToProps, { addItem })(Networking)
