import React, { Component } from 'react'
import { Table, Container, Button } from 'react-bootstrap'
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { addItem, getItems } from '../actions/tableActions';
import DataTable from 'react-data-table-component';



export class Networking extends Component {
  constructor(props) {
    super(props)

    this.state = {
      departmentName: '',
      listOfAssets: '',
      uniqueId: '',
      tableContent: []
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleClick = this.handleClick.bind(this);
  }

  static propTypes = {
    auth: PropTypes.object.isRequired,
    item: PropTypes.object.isRequired,
    addItem: PropTypes.func.isRequired,
    getItems: PropTypes.func.isRequired
  }

  handleClick(e) {
    e.preventDefault();

    // let randomID = Math.floor(Math.random() * 999999);
    var number = Math.random() // 0.9394456857981651
    number.toString(36); // '0.xtis06h6'
    var randomID = number.toString(36).substr(2, 9); // 'xtis06h6'
    // randomID.length >= 9; // false

    const { departmentName, listOfAssets, uniqueId, tableContent } = this.state

    let newItem = this.state.tableContent

    newItem.push({
      key: randomID,
      uniqueId: randomID,
      departmentName,
      listOfAssets
    });

    this.setState({
      tableContent: newItem
    })

    this.props.addItem(tableContent[0]);
  }

  handleChange(event) {
    this.setState({
      [event.target.name]: event.target.value
    });
  }

  componentDidMount() {
    this.props.getItems()
  }

  render() {
    const { isAuthenticated, user } = this.props.auth;
    const { tableContent } = this.state
    const { items } = this.props.item
    const details = items.map((newItem, index) => {
      newItem.tableContent.map(({ departmentName, listOfAssets, uniqueId }) => {
        console.log(departmentName, listOfAssets, uniqueId)
      })
    })
    const data = [{ tableContent }];

    const columns = [
      {
        name: 'Department Name',
        selector: 'title',
        sortable: true,
      },
      {
        name: 'List of Asset',
        selector: 'year',
        sortable: true,
        right: true,
      },
      {
        name: 'Unique Id',
        selector: 'year',
        sortable: true,
        right: true,
      },
    ];

    // console.log(items)

    const admin = (
      <form onSubmit={this.handleClick}>
        <label htmlFor="departmentName">Department Name</label>
        <input type="text" name="departmentName" onChange={this.handleChange} />

        <label htmlFor="Assets">Asset</label>
        <input type="text" name="listOfAssets" onChange={this.handleChange} />

        <button type="button" id="add" onClick={this.handleClick}>Add item</button>
      </form>
    )
    return (
      <section className="App">
        <header>
          <h1>Network Department</h1>
        </header>

        <section>
          <Container>
            {
              isAuthenticated && user.isAdmin ? admin : null
            }
          </Container>

          <section>
            <Container>
              <DataTable
                title="Arnold Movies"
                columns={columns}
                data={data}
              />
              {/* <Button onSubmit={this.handleSubmit}>Submit</Button> */}
            </Container>
          </section>
        </section>
      </section >
    );
  }
}

const mapStateToProps = state => ({
  auth: state.auth,
  item: state.item,
})

export default connect(mapStateToProps, { addItem, getItems })(Networking)
